import React, { useState, useCallback } from 'react';
import { X, Check } from 'lucide-react';
import './ImageCropper.css';

const ImageCropper = ({ 
  imageSrc, 
  onCrop, 
  onCancel, 
  aspectRatio = 1, 
  cropShape = 'rect' 
}) => {
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);

  // 获取裁剪区域坐标
  const onCropComplete = useCallback((_, croppedAreaPixels) => {
    setCroppedAreaPixels(croppedAreaPixels);
  }, []);

  // 创建图片对象
  const createImage = (url) =>
    new Promise((resolve, reject) => {
      const img = new Image();
      img.src = url;
      img.crossOrigin = "anonymous";
      img.onload = () => resolve(img);
      img.onerror = (e) => reject(e);
    });

  // 生成裁剪后的图片
  const getCroppedImg = async (imageSrc, crop) => {
    const image = await createImage(imageSrc);
    const canvas = document.createElement("canvas");
    const ctx = canvas.getContext("2d");

    canvas.width = crop.width;
    canvas.height = crop.height;

    // 如果是圆形裁剪
    if (cropShape === 'circle') {
      ctx.beginPath();
      ctx.arc(crop.width / 2, crop.height / 2, crop.width / 2, 0, 2 * Math.PI);
      ctx.clip();
    }

    ctx.drawImage(
      image,
      crop.x, crop.y, crop.width, crop.height, // 裁剪区域
      0, 0, crop.width, crop.height            // 输出大小
    );

    return new Promise((resolve) => {
      canvas.toBlob((blob) => {
        const reader = new FileReader();
        reader.onload = () => resolve(reader.result);
        reader.readAsDataURL(blob);
      }, "image/jpeg", 0.9);
    });
  };

  // 确认裁剪
  const handleConfirmCrop = useCallback(async () => {
    try {
      const croppedImageData = await getCroppedImg(imageSrc, croppedAreaPixels);
      onCrop(croppedImageData);
    } catch (e) {
      console.error('裁剪失败:', e);
    }
  }, [imageSrc, croppedAreaPixels, onCrop]);

  return (
    <div className="image-cropper">
      <div className="cropper-header">
        <h3>裁剪图片</h3>
        <div className="cropper-actions">
          <button className="cancel-btn" onClick={onCancel}>
            <X size={16} />
            取消
          </button>
          <button className="confirm-btn" onClick={handleConfirmCrop}>
            <Check size={16} />
            确认
          </button>
        </div>
      </div>

      <div className="cropper-body">
        <div className="cropper-container">
          <div className="crop-area">
            <img
              src={imageSrc}
              alt="裁剪预览"
              className="crop-image"
              style={{
                transform: `translate(${crop.x}px, ${crop.y}px) scale(${zoom})`,
                transformOrigin: 'center center'
              }}
            />
            
            {/* 裁剪框 */}
            <div 
              className={`crop-frame ${cropShape === 'circle' ? 'circle' : 'rect'}`}
              style={{
                width: '200px',
                height: '200px'
              }}
            >
              {/* 调整手柄 */}
              <div className="resize-handle nw"></div>
              <div className="resize-handle ne"></div>
              <div className="resize-handle sw"></div>
              <div className="resize-handle se"></div>
            </div>
          </div>
        </div>
      </div>

      <div className="cropper-controls">
        <div className="zoom-control">
          <label>缩放:</label>
          <input
            type="range"
            min={0.5}
            max={3}
            step={0.1}
            value={zoom}
            onChange={(e) => setZoom(parseFloat(e.target.value))}
          />
          <span>{zoom.toFixed(1)}x</span>
        </div>
      </div>

      <div className="cropper-footer">
        <p>拖拽图片调整位置，使用滑块调整缩放</p>
      </div>
    </div>
  );
};

export default ImageCropper;
