import React, { useState, useRef, useEffect } from 'react';
import { X, RotateCcw, Check } from 'lucide-react';
import './ImageCropper.css';

const ImageCropper = ({ 
  imageSrc, 
  onCrop, 
  onCancel, 
  aspectRatio = 1, 
  cropShape = 'rect' 
}) => {
  const [crop, setCrop] = useState({ x: 0, y: 0, width: 0, height: 0 });
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageSize, setImageSize] = useState({ width: 0, height: 0 });
  const [containerSize, setContainerSize] = useState({ width: 0, height: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [initialCrop, setInitialCrop] = useState({ x: 0, y: 0, width: 0, height: 0 });

  const containerRef = useRef(null);
  const imageRef = useRef(null);

  useEffect(() => {
    if (imageSrc) {
      const img = new Image();
      img.onload = () => {
        setImageSize({ width: img.width, height: img.height });
        setImageLoaded(true);
        
        // 计算容器尺寸
        const container = containerRef.current;
        if (container) {
          const containerRect = container.getBoundingClientRect();
          const maxWidth = containerRect.width - 40;
          const maxHeight = containerRect.height - 40;
          
          let displayWidth = img.width;
          let displayHeight = img.height;
          
          // 按比例缩放以适应容器
          if (displayWidth > maxWidth) {
            displayHeight = (displayHeight * maxWidth) / displayWidth;
            displayWidth = maxWidth;
          }
          if (displayHeight > maxHeight) {
            displayWidth = (displayWidth * maxHeight) / displayHeight;
            displayHeight = maxHeight;
          }
          
          setContainerSize({ width: displayWidth, height: displayHeight });
          
          // 初始化裁剪区域
          const cropSize = Math.min(displayWidth, displayHeight) * 0.8;
          const cropWidth = cropShape === 'circle' ? cropSize : cropSize;
          const cropHeight = cropShape === 'circle' ? cropSize : cropSize / aspectRatio;
          
          setCrop({
            x: (displayWidth - cropWidth) / 2,
            y: (displayHeight - cropHeight) / 2,
            width: cropWidth,
            height: cropHeight
          });
        }
      };
      img.src = imageSrc;
    }
  }, [imageSrc, aspectRatio, cropShape]);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX, y: e.clientY });
    setInitialCrop({ ...crop });
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;

    const deltaX = e.clientX - dragStart.x;
    const deltaY = e.clientY - dragStart.y;

    const newCrop = {
      x: Math.max(0, Math.min(containerSize.width - crop.width, initialCrop.x + deltaX)),
      y: Math.max(0, Math.min(containerSize.height - crop.height, initialCrop.y + deltaY)),
      width: crop.width,
      height: crop.height
    };

    setCrop(newCrop);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleResize = (direction, e) => {
    e.stopPropagation();
    const startX = e.clientX;
    const startY = e.clientY;
    const startCrop = { ...crop };

    const handleMouseMove = (moveEvent) => {
      const deltaX = moveEvent.clientX - startX;
      const deltaY = moveEvent.clientY - startY;

      let newCrop = { ...startCrop };

      switch (direction) {
        case 'se': // 右下角
          newCrop.width = Math.max(50, startCrop.width + deltaX);
          newCrop.height = newCrop.width / aspectRatio;
          break;
        case 'sw': // 左下角
          newCrop.width = Math.max(50, startCrop.width - deltaX);
          newCrop.height = newCrop.width / aspectRatio;
          newCrop.x = startCrop.x + (startCrop.width - newCrop.width);
          break;
        case 'ne': // 右上角
          newCrop.width = Math.max(50, startCrop.width + deltaX);
          newCrop.height = newCrop.width / aspectRatio;
          newCrop.y = startCrop.y + (startCrop.height - newCrop.height);
          break;
        case 'nw': // 左上角
          newCrop.width = Math.max(50, startCrop.width - deltaX);
          newCrop.height = newCrop.width / aspectRatio;
          newCrop.x = startCrop.x + (startCrop.width - newCrop.width);
          newCrop.y = startCrop.y + (startCrop.height - newCrop.height);
          break;
      }

      // 边界检查
      newCrop.x = Math.max(0, Math.min(containerSize.width - newCrop.width, newCrop.x));
      newCrop.y = Math.max(0, Math.min(containerSize.height - newCrop.height, newCrop.y));

      setCrop(newCrop);
    };

    const handleMouseUp = () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  const handleCrop = () => {
    if (!imageRef.current) return;

    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    
    // 设置画布尺寸
    canvas.width = crop.width;
    canvas.height = crop.height;

    // 计算实际图片中的裁剪区域
    const scaleX = imageSize.width / containerSize.width;
    const scaleY = imageSize.height / containerSize.height;

    const sourceX = crop.x * scaleX;
    const sourceY = crop.y * scaleY;
    const sourceWidth = crop.width * scaleX;
    const sourceHeight = crop.height * scaleY;

    // 如果是圆形裁剪
    if (cropShape === 'circle') {
      ctx.beginPath();
      ctx.arc(crop.width / 2, crop.height / 2, crop.width / 2, 0, 2 * Math.PI);
      ctx.clip();
    }

    // 绘制裁剪后的图片
    ctx.drawImage(
      imageRef.current,
      sourceX, sourceY, sourceWidth, sourceHeight,
      0, 0, crop.width, crop.height
    );

    // 转换为base64
    const croppedImageData = canvas.toDataURL('image/jpeg', 0.9);
    onCrop(croppedImageData);
  };

  const handleReset = () => {
    const cropSize = Math.min(containerSize.width, containerSize.height) * 0.8;
    const cropWidth = cropShape === 'circle' ? cropSize : cropSize;
    const cropHeight = cropShape === 'circle' ? cropSize : cropSize / aspectRatio;
    
    setCrop({
      x: (containerSize.width - cropWidth) / 2,
      y: (containerSize.height - cropHeight) / 2,
      width: cropWidth,
      height: cropHeight
    });
  };

  if (!imageSrc || !imageLoaded) {
    return (
      <div className="image-cropper">
        <div className="cropper-loading">加载中...</div>
      </div>
    );
  }

  return (
    <div className="image-cropper">
      <div className="cropper-header">
        <h3>裁剪图片</h3>
        <div className="cropper-actions">
          <button className="reset-btn" onClick={handleReset} title="重置">
            <RotateCcw size={16} />
          </button>
          <button className="cancel-btn" onClick={onCancel}>
            <X size={16} />
            取消
          </button>
          <button className="confirm-btn" onClick={handleCrop}>
            <Check size={16} />
            确认
          </button>
        </div>
      </div>

      <div className="cropper-body">
        <div 
          className="cropper-container"
          ref={containerRef}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
        >
          <img
            ref={imageRef}
            src={imageSrc}
            alt="裁剪预览"
            style={{
              width: containerSize.width,
              height: containerSize.height,
              cursor: isDragging ? 'grabbing' : 'grab'
            }}
            draggable={false}
          />

          {/* 裁剪遮罩 */}
          <div className="crop-overlay">
            <div 
              className="crop-selection"
              style={{
                left: crop.x,
                top: crop.y,
                width: crop.width,
                height: crop.height,
                borderRadius: cropShape === 'circle' ? '50%' : '0'
              }}
              onMouseDown={handleMouseDown}
            >
              {/* 调整手柄 */}
              <div className="resize-handle nw" onMouseDown={(e) => handleResize('nw', e)}></div>
              <div className="resize-handle ne" onMouseDown={(e) => handleResize('ne', e)}></div>
              <div className="resize-handle sw" onMouseDown={(e) => handleResize('sw', e)}></div>
              <div className="resize-handle se" onMouseDown={(e) => handleResize('se', e)}></div>
            </div>
          </div>
        </div>
      </div>

      <div className="cropper-footer">
        <p>拖拽移动裁剪区域，拖拽角落调整大小</p>
      </div>
    </div>
  );
};

export default ImageCropper;
