import React, { useState } from 'react';
import { ArrowLeft, Star, MessageCircle } from 'lucide-react';
import './CharacterSelector.css';

const CharacterSelector = ({ characters, onSelect, userName }) => {
  const [selectedId, setSelectedId] = useState(null);

  const handleCharacterClick = (character) => {
    setSelectedId(character.id);
  };

  const handleStartChat = () => {
    const character = characters.find(c => c.id === selectedId);
    if (character) {
      onSelect(character);
    }
  };

  return (
    <div className="character-selector">
      <div className="selector-header">
        <h2>你好，{userName}！</h2>
        <p>选择一个角色开始你的对话之旅</p>
      </div>

      <div className="characters-grid">
        {characters.map((character) => (
          <div
            key={character.id}
            className={`character-card ${selectedId === character.id ? 'selected' : ''}`}
            onClick={() => handleCharacterClick(character)}
          >
            <div className="character-avatar">
              <span className="avatar-emoji">{character.avatar}</span>
            </div>
            
            <div className="character-info">
              <h3>{character.name}</h3>
              <p className="character-description">{character.description}</p>
              
              <div className="character-personality">
                <strong>性格特点：</strong>
                <span>{character.personality}</span>
              </div>

              <div className="character-skills">
                <strong>技能特长：</strong>
                <div className="skills-list">
                  {character.skills.map((skill, index) => (
                    <span key={index} className="skill-tag">
                      {skill}
                    </span>
                  ))}
                </div>
              </div>
            </div>

            <div className="character-actions">
              <button 
                className="select-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  handleCharacterClick(character);
                }}
              >
                <MessageCircle size={16} />
                选择对话
              </button>
            </div>
          </div>
        ))}
      </div>

      {selectedId && (
        <div className="selection-actions">
          <button 
            className="start-chat-btn"
            onClick={handleStartChat}
          >
            <Star size={20} />
            开始与 {characters.find(c => c.id === selectedId)?.name} 对话
          </button>
        </div>
      )}

      <div className="selector-footer">
        <p>💡 提示：每个角色都有独特的性格和知识背景，选择你感兴趣的角色开始对话吧！</p>
      </div>
    </div>
  );
};

export default CharacterSelector;
