import React, { useState, useCallback, useRef, useEffect } from 'react';
import { X, Check } from 'lucide-react';
import './ImageCropper.css';

const ImageCropper = ({ 
  imageSrc, 
  onCrop, 
  onCancel, 
  aspectRatio = 1, 
  cropShape = 'circle' 
}) => {
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageSize, setImageSize] = useState({ width: 0, height: 0 });
  const [displaySize, setDisplaySize] = useState({ width: 0, height: 0 });
  
  const containerRef = useRef(null);
  const imageRef = useRef(null);
  const isDragging = useRef(false);
  const dragStart = useRef({ x: 0, y: 0 });

  useEffect(() => {
    if (imageSrc) {
      const img = new Image();
      img.onload = () => {
        setImageSize({ width: img.width, height: img.height });
        setImageLoaded(true);
        
        // 计算图片在容器中的显示尺寸，确保完全填充
        const container = containerRef.current;
        if (container) {
          const containerRect = container.getBoundingClientRect();
          const containerWidth = containerRect.width;
          const containerHeight = containerRect.height;
          
          const imageAspect = img.width / img.height;
          const containerAspect = containerWidth / containerHeight;
          
          let displayWidth, displayHeight;
          
          if (imageAspect > containerAspect) {
            // 图片更宽，以高度为准
            displayHeight = containerHeight;
            displayWidth = displayHeight * imageAspect;
          } else {
            // 图片更高，以宽度为准
            displayWidth = containerWidth;
            displayHeight = displayWidth / imageAspect;
          }
          
          setDisplaySize({ width: displayWidth, height: displayHeight });
        }
      };
      img.src = imageSrc;
    }
  }, [imageSrc]);

  // 创建图片对象
  const createImage = (url) =>
    new Promise((resolve, reject) => {
      const img = new Image();
      img.src = url;
      img.crossOrigin = "anonymous";
      img.onload = () => resolve(img);
      img.onerror = (e) => reject(e);
    });

  // 生成裁剪后的图片
  const getCroppedImg = async (imageSrc, cropPosition) => {
    const image = await createImage(imageSrc);
    const canvas = document.createElement("canvas");
    const ctx = canvas.getContext("2d");

    const cropSize = 200; // 固定裁剪尺寸
    canvas.width = cropSize;
    canvas.height = cropSize;

    // 计算裁剪区域在原始图片中的位置
    const scaleX = imageSize.width / displaySize.width;
    const scaleY = imageSize.height / displaySize.height;
    
    // 计算裁剪框在显示图片中的位置
    const cropX = (displaySize.width - cropSize) / 2 + cropPosition.x;
    const cropY = (displaySize.height - cropSize) / 2 + cropPosition.y;
    
    // 转换为原始图片坐标
    const sourceX = cropX * scaleX;
    const sourceY = cropY * scaleY;
    const sourceWidth = cropSize * scaleX;
    const sourceHeight = cropSize * scaleY;

    // 如果是圆形裁剪
    if (cropShape === 'circle') {
      ctx.beginPath();
      ctx.arc(cropSize / 2, cropSize / 2, cropSize / 2, 0, 2 * Math.PI);
      ctx.clip();
    }

    ctx.drawImage(
      image,
      sourceX, sourceY, sourceWidth, sourceHeight,
      0, 0, cropSize, cropSize
    );

    return new Promise((resolve) => {
      canvas.toBlob((blob) => {
        const reader = new FileReader();
        reader.onload = () => resolve(reader.result);
        reader.readAsDataURL(blob);
      }, "image/jpeg", 0.9);
    });
  };

  // 处理鼠标按下
  const handleMouseDown = (e) => {
    e.preventDefault();
    isDragging.current = true;
    dragStart.current = { x: e.clientX - crop.x, y: e.clientY - crop.y };
  };

  // 处理鼠标移动
  const handleMouseMove = (e) => {
    if (!isDragging.current) return;
    e.preventDefault();
    
    const newX = e.clientX - dragStart.current.x;
    const newY = e.clientY - dragStart.current.y;
    
    // 限制拖拽范围
    const maxX = (containerSize.width - 200) / 2;
    const maxY = (containerSize.height - 200) / 2;
    
    setCrop({
      x: Math.max(-maxX, Math.min(maxX, newX)),
      y: Math.max(-maxY, Math.min(maxY, newY))
    });
  };

  // 处理鼠标释放
  const handleMouseUp = () => {
    isDragging.current = false;
  };

  // 确认裁剪
  const handleConfirmCrop = useCallback(async () => {
    try {
      const croppedImageData = await getCroppedImg(imageSrc, crop);
      onCrop(croppedImageData);
    } catch (e) {
      console.error('裁剪失败:', e);
    }
  }, [imageSrc, crop, onCrop]);

  return (
    <div className="image-cropper">
      <div className="cropper-header">
        <h3>裁剪图片</h3>
        <div className="cropper-actions">
          <button className="cancel-btn" onClick={onCancel}>
            <X size={16} />
            取消
          </button>
          <button className="confirm-btn" onClick={handleConfirmCrop}>
            <Check size={16} />
            确认
          </button>
        </div>
      </div>

      <div className="cropper-body">
        <div 
          className="cropper-container"
          ref={containerRef}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
        >
          {imageLoaded && (
            <>
              <img
                ref={imageRef}
                src={imageSrc}
                alt="裁剪预览"
                className="crop-image"
                style={{
                  width: containerSize.width,
                  height: containerSize.height,
                  transform: `translate(${crop.x}px, ${crop.y}px) scale(${zoom})`,
                  transformOrigin: 'center center'
                }}
                onMouseDown={handleMouseDown}
                draggable={false}
              />
              
              {/* 裁剪框 */}
              <div 
                className={`crop-frame ${cropShape === 'circle' ? 'circle' : 'rect'}`}
              />
            </>
          )}
        </div>
      </div>

      <div className="cropper-controls">
        <div className="zoom-control">
          <label>缩放:</label>
          <input
            type="range"
            min={0.5}
            max={3}
            step={0.1}
            value={zoom}
            onChange={(e) => setZoom(parseFloat(e.target.value))}
          />
          <span>{zoom.toFixed(1)}x</span>
        </div>
      </div>

      <div className="cropper-footer">
        <p>拖拽图片调整位置，使用滑块调整缩放</p>
      </div>
    </div>
  );
};

export default ImageCropper;
