import React, { useState } from 'react';
import './App.css';
import CharacterSelector from './components/CharacterSelector';
import ChatInterface from './components/ChatInterface';
import UserProfile from './components/UserProfile';
import CharacterEditModal from './components/CharacterEditModal';
import { characters } from './data/characters';

function App() {
  const [selectedCharacter, setSelectedCharacter] = useState(null);
  const [userName, setUserName] = useState('');
  const [userAvatar, setUserAvatar] = useState(null);
  const [isProfileComplete, setIsProfileComplete] = useState(false);
  const [charactersList, setCharactersList] = useState(characters);
  const [editingCharacter, setEditingCharacter] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  const handleCharacterSelect = (character) => {
    setSelectedCharacter(character);
  };

  const handleUserNameSubmit = (name, avatar) => {
    setUserName(name);
    setUserAvatar(avatar);
    setIsProfileComplete(true);
  };

  const handleBackToSelection = () => {
    setSelectedCharacter(null);
    // ‰øùÊåÅÁî®Êà∑Â∑≤ÁôªÂΩïÁä∂ÊÄÅÔºå‰∏çÈáçÁΩÆ isProfileComplete
    console.log('ËøîÂõûËßíËâ≤ÈÄâÊã©ÔºåÁî®Êà∑‰øùÊåÅÁôªÂΩïÁä∂ÊÄÅ:', userName);
  };

  const handleEditCharacter = (character) => {
    setEditingCharacter(character);
    setIsEditModalOpen(true);
  };

  const handleSaveCharacter = (updatedCharacter) => {
    setCharactersList(prev => 
      prev.map(char => 
        char.id === updatedCharacter.id ? updatedCharacter : char
      )
    );
    setIsEditModalOpen(false);
    setEditingCharacter(null);
  };

  const handleCloseEditModal = () => {
    setIsEditModalOpen(false);
    setEditingCharacter(null);
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>üé≠ AIËßíËâ≤ÊâÆÊºîËÅäÂ§©</h1>
        <p>‰∏éÂéÜÂè≤‰∫∫Áâ©ÂíåËôöÊûÑËßíËâ≤ËøõË°åÊ∑±Â∫¶ÂØπËØù</p>
      </header>

      <main className="app-main">
        {!isProfileComplete ? (
          <UserProfile onSubmit={handleUserNameSubmit} />
        ) : !selectedCharacter ? (
          <CharacterSelector 
            characters={charactersList} 
            onSelect={handleCharacterSelect}
            userName={userName}
            onEditCharacter={handleEditCharacter}
          />
        ) : (
          <ChatInterface 
            character={selectedCharacter}
            userName={userName}
            onBack={handleBackToSelection}
          />
        )}
      </main>

      <footer className="app-footer">
        <p>¬© 2025 AIËßíËâ≤ÊâÆÊºîËÅäÂ§© - Êé¢Á¥¢Êó†ÈôêÂèØËÉΩ</p>
      </footer>

      <CharacterEditModal
        character={editingCharacter}
        isOpen={isEditModalOpen}
        onClose={handleCloseEditModal}
        onSave={handleSaveCharacter}
      />
    </div>
  );
}

export default App;
