import React, { useState, useEffect } from 'react';
import { User, ArrowRight } from 'lucide-react';
import './UserProfile.css';
import ImageUpload from './ImageUpload';
import imageService from '../services/imageService';

const UserProfile = ({ onSubmit }) => {
  const [userName, setUserName] = useState('');
  const [isValid, setIsValid] = useState(false);
  const [userAvatar, setUserAvatar] = useState(null);

  // Âä†ËΩΩÂ∑≤‰øùÂ≠òÁöÑÁî®Êà∑Â§¥ÂÉè
  useEffect(() => {
    const savedAvatar = imageService.getUserAvatar('current_user');
    if (savedAvatar) {
      setUserAvatar(savedAvatar);
    }
  }, []);

  const handleInputChange = (e) => {
    const name = e.target.value.trim();
    setUserName(name);
    setIsValid(name.length >= 2);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isValid) {
      onSubmit(userName, userAvatar);
    }
  };

  const handleAvatarChange = (imageData) => {
    setUserAvatar(imageData);
    if (imageData) {
      // ‰øùÂ≠òÂà∞Êú¨Âú∞Â≠òÂÇ®
      imageService.saveUserAvatar('current_user', imageData);
      console.log('Áî®Êà∑Â§¥ÂÉèÂ∑≤Êõ¥Êñ∞');
    }
  };

  return (
    <div className="user-profile">
      <div className="profile-card">
        <div className="profile-avatar-section">
          <ImageUpload
            currentImage={userAvatar}
            onImageChange={handleAvatarChange}
            type="avatar"
            size="large"
            className="avatar"
          />
        </div>
        
        <h2>Ê¨¢ËøéÊù•Âà∞AIËßíËâ≤ÊâÆÊºî‰∏ñÁïå</h2>
        <p className="profile-description">
          Âú®ËøôÈáåÔºå‰Ω†ÂèØ‰ª•‰∏éÂéÜÂè≤‰∫∫Áâ©ÂíåËôöÊûÑËßíËâ≤ËøõË°åÊ∑±Â∫¶ÂØπËØù„ÄÇ
          È¶ñÂÖàÔºåËØ∑ÂëäËØâÊàë‰ª¨‰Ω†ÁöÑÂêçÂ≠óÔºåËøôÊ†∑ËßíËâ≤‰ª¨Â∞±ËÉΩÊõ¥Â•ΩÂú∞ËÆ§ËØÜ‰Ω†„ÄÇ
        </p>

        <form onSubmit={handleSubmit} className="profile-form">
          <div className="input-group">
            <label htmlFor="userName">‰Ω†ÁöÑÂêçÂ≠ó</label>
            <input
              type="text"
              id="userName"
              value={userName}
              onChange={handleInputChange}
              placeholder="ËØ∑ËæìÂÖ•‰Ω†ÁöÑÂêçÂ≠ó"
              maxLength={20}
              required
            />
            <span className="input-hint">Ëá≥Â∞ë2‰∏™Â≠óÁ¨¶</span>
          </div>

          <button 
            type="submit" 
            className={`submit-btn ${isValid ? 'valid' : 'invalid'}`}
            disabled={!isValid}
          >
            <span>ÂºÄÂßãÊé¢Á¥¢</span>
            <ArrowRight size={20} />
          </button>
        </form>

        <div className="features-preview">
          <h3>‰Ω†Â∞Ü‰ΩìÈ™åÂà∞Ôºö</h3>
          <ul>
            <li>üé≠ ‰∏éÂ§ö‰∏™AIËßíËâ≤ËøõË°åÂØπËØù</li>
            <li>üé§ ËØ≠Èü≥ËÅäÂ§©ÂäüËÉΩ</li>
            <li>üß† Êô∫ËÉΩËßíËâ≤ÊâÆÊºî</li>
            <li>üìö Â≠¶‰π†ÂéÜÂè≤ÂíåÊñáÂåñÁü•ËØÜ</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

